mapper.select:
SELECT SQBZJ, SQDXBH, SQZYBM, SQZYLX, QXYXDTDBH, QXSXSJ, QXGQSJ, CJR, CJSJ, ZHXGR, ZHXGSJ, JLZT
FROM PF_ARCGRANT WHERE SQDXBH = #{sqdxbh,jdbcType=VARCHAR} AND SQZYBM = #{sqzybm,jdbcType=VARCHAR}
AND SQZYLX = #{sqzylx,jdbcType=CHAR} AND JLZT = '0'

SELECT * FROM PF_ARCGRANT WHERE 1=1
 AND SQDXBH = #{sqdxbh,jdbcType=VARCHAR}
 AND SQZYBM = #{sqzybm,jdbcType=VARCHAR}
 AND SQZYLX = #{sqzylx,jdbcType=CHAR}
 AND JLZT = #{jlzt,jdbcType=CHAR}

mapper.update:
update PF_ARCGRANT
SQDXBH = #{sqdxbh,jdbcType=VARCHAR}, SQZYBM = #{sqzybm,jdbcType=VARCHAR}, SQZYLX = #{sqzylx,jdbcType=CHAR}, QXYXDTDBH = #{qxyxdtdbh,jdbcType=VARCHAR}, QXSXSJ = #{qxsxsj,jdbcType=TIMESTAMP}, QXGQSJ = #{qxgqsj,jdbcType=TIMESTAMP}, CJR = #{cjr,jdbcType=CHAR}, CJSJ = #{cjsj,jdbcType=TIMESTAMP}, ZHXGR = #{zhxgr,jdbcType=CHAR}, ZHXGSJ = #{zhxgsj,jdbcType=TIMESTAMP}, JLZT = #{jlzt,jdbcType=CHAR},
where SQBZJ = #{sqbzj,jdbcType=CHAR}
