mapper.select:
		SELECT CDBH, CDBM, CDMC, CDURL, SJCDBH, CDSXH, CDTP, SFXDLJ, CDDKCKSZWZ, CDZXKJBH, CDSFKY, CDSFKJ, CDDKFS, CDLX FROM PF_RESMENU
		WHERE 1=1
		 AND CDMC = #{cdbm,jdbcType=VARCHAR}
		 AND CDBH = #{cdbh,jdbcType=CHAR}
        START WITH SJCDBH = 'DEFAULT_MENUTREE_ROOT' CONNECT BY PRIOR CDBH = SJCDBH ORDER SIBLINGS BY CDSXH ASC

mapper.update:
update PF_RESMENU
CDBM = #{cdbm,jdbcType=VARCHAR}, CDMC = #{cdmc,jdbcType=VARCHAR}, CDURL = #{cdurl,jdbcType=VARCHAR}, SJCDBH = #{sjcdbh,jdbcType=VARCHAR}, CDSXH = #{cdsxh,jdbcType=DECIMAL}, CDTP = #{cdtp,jdbcType=VARCHAR}, SFXDLJ = #{sfxdlj,jdbcType=CHAR}, CDDKCKSZWZ = #{cddkckszwz,jdbcType=VARCHAR}, CDZXKJBH = #{cdzxkjbh,jdbcType=VARCHAR}, CDSFKY = #{cdsfky,jdbcType=CHAR}, CDSFKJ = #{cdsfkj,jdbcType=CHAR}, CDDKFS = #{cddkfs,jdbcType=CHAR}, CDLX = #{cdlx,jdbcType=CHAR},
where CDBH = #{cdbh,jdbcType=CHAR}

mapper.select:
select distinct m.cdbh,m.cdmc,m.sjcdbh,m.cdurl,m.cdsxh,m.cdtp,m.cdsfkj,m.cdsfky,m.cddkfs from PF_RESMENU m , PF_ARCGRANT g where m.cdbh = g.sqzybm and g.sqzylx = 'CD' and g.jlzt = '1' and m.cdsfkj = '1' and g.sqdxbh in
#{id}
AND pid=#{pid} AND (m.CDLX IN ('0','1') OR m.CDLX IS NULL ) AND (m.CDLX IN ('0','2') OR m.CDLX IS NULL ) ORDER BY m.cdsxh,m.cdmc
