mapper.select:
select JSBH, JSMC, JSLX, JSZT, JSMS, CJR, CJSJ, ZHXGR, ZHXGSJ, JSQZ, JLZT, KZZD1, KZZD2, KZZD3, KZZD4, KZZD5, KZZD6
from PF_ROLE WHERE 1=1
 AND JSMC LIKE '%'||#{jsmc,jdbcType=VARCHAR}||'%'
 AND JSLX = #{jslx,jdbcType=CHAR}
 AND JSZT = #{jszt,jdbcType=CHAR}
AND JLZT = '1'

        select COUNT(1)
        from PF_ROLE WHERE 1=1
		 AND JSMC LIKE '%'||#{jsmc,jdbcType=VARCHAR}||'%'
		 AND JSLX = #{jslx,jdbcType=CHAR}
		 AND JSZT = #{jszt,jdbcType=CHAR}
		AND JLZT = '1'

mapper.update:
update PF_ROLE
JSMC = #{jsmc,jdbcType=VARCHAR}, JSLX = #{jslx,jdbcType=CHAR}, JSZT = #{jszt,jdbcType=CHAR}, JSMS = #{jsms,jdbcType=VARCHAR}, CJR = #{cjr,jdbcType=CHAR}, CJSJ = #{cjsj,jdbcType=TIMESTAMP}, ZHXGR = #{zhxgr,jdbcType=CHAR}, ZHXGSJ = #{zhxgsj,jdbcType=TIMESTAMP}, JSQZ = #{jsqz,jdbcType=DECIMAL}, JLZT = #{jlzt,jdbcType=CHAR}, KZZD1 = #{kzzd1,jdbcType=VARCHAR}, KZZD2 = #{kzzd2,jdbcType=VARCHAR}, KZZD3 = #{kzzd3,jdbcType=VARCHAR}, KZZD4 = #{kzzd4,jdbcType=VARCHAR}, KZZD5 = #{kzzd5,jdbcType=VARCHAR}, KZZD6 = #{kzzd6,jdbcType=VARCHAR},
where JSBH = #{jsbh,jdbcType=VARCHAR}
